mapper.select:
SELECT COUNT( 1 ) as value, airq_level.level_grade as name, airq_level.`level` AS level FROM airq_level LEFT JOIN airq_day ON airq_day.`LEVEL` = airq_level.`level` WHERE airq_day.MN IN (${mns}) AND airq_level.`level` < 7
AND DATA_TIME BETWEEN #{timeStart} AND #{timeEnd}
GROUP BY airq_day.`LEVEL` UNION all (select 0 as number ,level_grade,`level` from airq_level where level_grade not in ( SELECT level_grade FROM airq_level LEFT JOIN airq_day ON airq_day.`LEVEL` = airq_level.`level` WHERE airq_day.MN IN (${mns}) AND airq_level.`level` < 7
AND DATA_TIME BETWEEN #{timeStart} AND #{timeEnd}
GROUP BY airq_day.`LEVEL` ) AND airq_level.`level` < 7 ) ORDER BY level

select a.mn, company_Id, area, site_Name, data_Time, aqi, first_Code, level, a21026_Avg, a21026_Iaqi, a21004_Avg, a21004_Iaqi, a3400224_Avg, a3400224_Iaqi, a21005_Avg, a21005_Iaqi, a0502401_Avg, a0502401_Iaqi, a0502408_Avg, a0502408_Iaqi, a3400424_Avg, a3400424_Iaqi from site_monitor_point a left join airq_day b on a.mn = b.MN where site_type = '3' and b.data_time >= #{datatime} and b.data_time <= #{datatime2} and company_id in
#{id}
and a.area = #{area}
and a.mn = #{mn}

select data_Time dataTime, aqi AQI, A3400424_IAQI A34004, A3400224_IAQI A34002, A21026_IAQI A21026, A21005_IAQI A21005, A21004_IAQI A21004, A0502408_IAQI A05024, FIRST_CODE firstCode from site_monitor_point a left join airq_day b on a.mn = b.MN where site_type = '3' and b.data_time >= #{datatime} and b.data_time <= #{datatime2}
and a.area = #{area}
and a.id in
#{checkedKey}
order by dataTime

select AVG(a.aqi) AQI,a.data_time from airq_day a left join site_monitor_point b ON a.mn = b.mn WHERE b.site_type='3' AND b.company_id IN
#{id}
AND a.data_time BETWEEN (#{startTime}) AND (#{endTime}) GROUP BY a.data_time order by a.data_time asc
