mapper.select:
select a.mn mn, a.site_name siteName, a.site_type siteType, a.company_id companyId, b.company_name companyName from site_monitor_point a left join company_base b on a.company_id=b.id where a.site_state='1' and a.site_type in ('0','1','2') and a.company_id in
#{id}
and a.company_id in (select N.company_id from company_baseinfo N WHERE N.status='2' and N.administrative_region=#{area} and N.company_id in
#{id} )
and a.site_type=#{siteType}
order by a.company_id

select a.id id
from warn_point_rule a left join warn_rule b on a.rule_id=b.id
where a.mn in

    #{id}

and b.rule_type in

    #{id}

select a.id id
from warn_point_rule a left join warn_rule b on a.rule_id=b.id
where a.mn in

    #{id}

and b.rule_type=#{ruleType} and b.site_type=#{siteType} and b.code = #{code}

select e.administrative_region area, c.company_id companyId, d.company_name companyName, a.mn mn, c.site_name siteName, b.rule_type ruleType from warn_point_rule a LEFT JOIN warn_rule b on a.rule_id = b.id LEFT JOIN site_monitor_point c on a.mn= c.mn LEFT JOIN company_base d on c.company_id = d.id LEFT JOIN company_baseinfo e on e.company_id=c.company_id WHERE a.is_used ='0' and e.`status`='2' and c.company_id in
#{id}
GROUP BY a.mn,b.rule_type order by c.company_id,a.mn
